from langchain_google_genai import ChatGoogleGenerativeAIimport osfrom load_dotenv import load_dotenvload_dotenv()llm=ChatGoogleGenerativeAI(model="gemini-pro",                           verbose=True,                           temperature=0.5,                           google_api_key=os.getenv("GEMINI_API_KEY"),)def read_srt(file_path):    with open(file_path, 'r', encoding='utf-8') as file:        return file.read()srt_content = read_srt('srts/subtitles.srt')prompt = "You are a very helpful assistant , Write a blog post based on the following subtitle content in the markdown format: \n"full_prompt = prompt + srt_content# Generate a responseresponse = llm.predict(full_prompt)print(response)output_file = 'Blogs/blog_post.md'with open(output_file, 'w', encoding='utf-8') as file:    file.write(response)print(f"Response saved to {output_file}")